const App: React.FC = () => {
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);
  const [forgivenessLevel, setForgivenessLevel] = useState(15);
  const [isMonkeyClicked, setIsMonkeyClicked] = useState(false);
